import React, { useState, Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import DeleteConfirmModal from '../DeleteConfirmModal';
import * as css from "./StylesProject.css";
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import projectsActions from '../../store/Actions'
import axios from 'axios';
import { connect } from 'react-redux';
import '../../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';


const proj = {
    id: 1,
    title: "Yee",
    description: "!!!!",
    dateCreate: "12.01.2019",
    countUsers: 10,
    creator: "Барабашка",
    users: [
        {
            id: 1,
            firstName: "Inna",
            lastName: "Shutova",
            countActiveTasks: 19,
            email: "i.schutova@nordclan.com"
        },
        {
            id: 2,
            firstName: "Natalia",
            lastName: "Myasnikova",
            countActiveTasks: 19,
            email: "i.schutova@nordclan.com"
        }
    ]
};



class Project extends Component {
    constructor(props) {
        super(props);

        console.log(props);
        console.log(props.location.pathname.substring(props.location.pathname.lastIndexOf('/')+1));
        this.state = {
            project: {},
            isShow: false,
            isRequested: false,
            projectId: Number.parseInt(props.location.pathname.substring(props.location.pathname.lastIndexOf('/')+1))
        };
    }

    componentDidMount = () => {
        this.getProjectHandler("", this.state.projectId);
    };

    setShow = (value) => {
        this.setState({ isShow: value });
    }

    getProjectHandler = (apikey, projectId) => {
        axios.get('http://localhost:57392/projectInfo?apikey=qwerty&project_id='+projectId)
        .then(response => {
            console.log(response);
            this.setState({project: response.data.data, isRequested: true});
        });
    }

    deleteProjectHandler = (projectId) => {
        axios.delete('http://localhost:57392/projectInfo',
        {params:{
            apikey: "qwerty",
            project_id: projectId
        }})
        .then(response => {
            console.log(response);
           // this.setState({project: response.data.data, isRequested: true});
        });
    }

    render() {
        console.log(this.props);
        console.log(this.state);
        if (this.state.isRequested) {
            return <div className="main_fon">
            <div className="inline_block">
                <div className="title_block">
                    <div className="text_title"> {this.state.project.project_name} </div>
                    <br />
                    <div className="text_author">
                        Автор: {this.state.project.creater_first_name + " " + 
                        this.state.project.creater_last_name}
                    </div>
                </div>
    
                <Button className="button_padding" variant="outline-primary">Выгрузить статистику</Button>
                <Button onClick={()=>this.setShow(true)} className="button_padding" variant="outline-primary">Удалить проект</Button>
            </div>
            <div className="text_description">
                {this.state.project.description}
            </div>
    
            {
                proj.users.lenth !== 0 && <div className="users_list">
                    <div className="inline_block_users_title">
                        <div className="title_block">Список пользователей</div>
                        <Button className="button_padding" variant="outline-primary">Добавить пользователя</Button>
                    </div>
                    {
                        proj.users.map(user => {
                            return <ListGroup.Item className="inline_block_elements">
                                <div className="inline_block_users">{user.firstName}</div>
                                <div className="inline_block_users">{user.lastName}</div>
                                <div className="inline_block_users">{user.email}</div>
                            </ListGroup.Item>;
                        })
                    }
                </div>
            }
    
            <div className="inline_block_users_title">Задачи по проекту</div>
            <div className="text_description">
                <BootstrapTable data={[]} striped hover>
                    <TableHeaderColumn isKey dataField='taskId'>Id задачи</TableHeaderColumn>
                    <TableHeaderColumn dataField='name'>Название задачи</TableHeaderColumn>
                    <TableHeaderColumn dataField='taskDate'>Дата постановки</TableHeaderColumn>
                    <TableHeaderColumn dataField='id'>Id исполнителя</TableHeaderColumn>
                    <TableHeaderColumn dataField='time'>Затраченное время</TableHeaderColumn>
                    <TableHeaderColumn dataField='status'>Статус</TableHeaderColumn>
                </BootstrapTable>
            </div>
            
            <DeleteConfirmModal show={this.state.isShow} 
            onHide={() => this.setShow(false)} 
            onSave={(projectId)=> this.deleteProjectHandler(projectId)} />

            </div>;
        } else {
            return <div>Loading...</div>
        }
       
    }
}

const mapStateToProps = (state) => ({
    project: state.Project,
    isShow: false,
    isRequested: state.Projects ? state.Projects.IsRequested : false,
    projectId: 0
});
export default connect(mapStateToProps, null)(Project);
