import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import { connect } from 'react-redux';
import axios from 'axios';
import Moment from 'react-moment';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';
import DeleteConfirmModal from '../DeleteConfirmModal';
import * as css from "./StylesUserPage.css";


class UserPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: {
                first_name: '',
                last_name: '',
                is_admin: '',
                birth_date: new Date()
            },
            isRequested: false,
            isShow: false,
            userId: Number.parseInt(props.location.pathname.substring(props.location.pathname.lastIndexOf('/') + 1))
        };
    }

    componentDidMount = () => {
        this.getUserInfoHandler("");
    };

    getUserInfoHandler = (apikey) => {
        axios.get('http://localhost:57392/admin?apikey=qwerty&user_id=' + this.state.userId)
            .then(response => {
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.setState({ user: response.data.data, isRequested: true });
                }
            });
    }

    setShow = (value) => {
        this.setState({ isShow: value });
    }

    deleteUserHandler = (userId) => {
        axios.post('http://localhost:57392/userPage', {
            apikey: "qwerty",
            user_id: userId
        })
            .then(response => {
                console.log(response);
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.props.history.push(`/users`);
                }
            });
    }

    render() {
        if (this.state.isRequested) {
            console.log(this.state.user);
            return <div className="main_fon">
                <div className="inline">
                    <div className="headingPage">
                        Информация о пользователе
                    </div>
                    <Button className="lineValueButton" onClick={() => this.setShow(true)} variant="primary" type="submit">
                        Удалить
                    </Button>
                </div>
                <div className="form_class">
                    <Form>
                        <Form.Group as={Row} controlId="formHorizontalEmail">
                            <Form.Label className="boldLineHead" column sm={2}>
                                Имя
                        </Form.Label>
                            <Col sm={10}>
                                <Form.Label column sm={2}>
                                    {
                                        this.state.user.first_name
                                    }
                                </Form.Label>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="formHorizontalEmail">
                            <Form.Label className="boldLineHead" column sm={2}>
                                Фамилия
                        </Form.Label>
                            <Col sm={10}>
                                <Form.Label column sm={2}>
                                    {
                                        this.state.user.last_name
                                    }
                                </Form.Label>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="formHorizontalEmail">
                            <Form.Label className="boldLineHead" column sm={2}>
                                Номер телефона
                        </Form.Label>
                            <Col sm={10}>
                                <Form.Label column sm={2}>
                                    {
                                        this.state.user.phone
                                    }
                                </Form.Label>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="formHorizontalEmail">
                            <Form.Label className="boldLineHead" column sm={2}>
                                Email
                        </Form.Label>
                            <Col sm={10}>
                                <Form.Label column sm={2}>
                                    {
                                        this.state.user.email
                                    }
                                </Form.Label>
                            </Col>
                        </Form.Group>
                        <Form.Group as={Row} controlId="formHorizontalEmail">
                            <Form.Label className="boldLineHead" column sm={2}>
                                Дата рождения
                        </Form.Label>
                            <Col sm={10}>
                                <Form.Label column sm={2}>
                                    <Moment format="DD.MM.YYYY">
                                        {
                                            new Date(this.state.user.birth_date)
                                        }
                                    </Moment>
                                </Form.Label>
                            </Col>
                        </Form.Group>
                    </Form>
                </div>

                <div className="inline_block_users_title">
                        Задачи пользователя
                </div>
                <div className="text_description">
                    <BootstrapTable data={this.state.user.tasks_list} striped hover>
                        <TableHeaderColumn isKey dataField='task_id'>Id задачи</TableHeaderColumn>
                        <TableHeaderColumn dataField='task_name'>Название задачи</TableHeaderColumn>
                        <TableHeaderColumn dataField='create_date'>Дата постановки</TableHeaderColumn>
                        <TableHeaderColumn dataField='task_status_text'>Статус</TableHeaderColumn>
                    </BootstrapTable>
                </div>

                <DeleteConfirmModal show={this.state.isShow}
                    projectId={this.state.userId}
                    onHide={() => this.setShow(false)}
                    onSave={(userId) => this.deleteUserHandler(userId)} />

            </div>;

        } else {
            return <div>Loading...</div>
        }
    };
}

const mapStateToProps = (state) => ({
    user: state.User,
    userId: 0,
    isShow: false
});

export default connect(
    mapStateToProps,
    null
)(UserPage);
