import React, { Component } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import { connect } from 'react-redux';


class AddUserInProjectModal extends Component {
    constructor(props) {
        super(props);
        console.log(props);
        this.state = {
            projectId: props.projectId,
            users: []
        };
    }

    componentDidMount = () => {
        this.getUsersListHandler();
    };

    getUsersListHandler = () =>{
        axios.get('http://localhost:57392/userToProject?apikey=qwerty&project_id='+this.state.projectId)
        .then(response => {
            console.log(response);
            this.setState({projects: response.data.data, isRequested: true});
        });
    }

    render() {
        console.log(this.state);
        console.log(this.props);
        console.log(this.props.projectId);
        return (
            <Modal
                show={this.props.show}
                onHide={this.props.onHide}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Добавить пользователей
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {
                        this.state.users.length !== 0 &&
                        this.state.users.map(user=>{
                            return <Form.Group controlId="formBasicCheckbox">
                        <Form.Label>{user.first_name}</Form.Label>
                            <Form.Check type="checkbox" label="Check me out" />
                        </Form.Group>
                        })
                    }
                    
                </Modal.Body>
                <Modal.Footer>
                    <Button 
                    onClick={()=>this.props.onSave(this.props.projectId)} 
                    variant="primary" 
                    type="submit">
                        Выполнить
                    </Button>
                    <Button onClick={this.props.onHide}>Закрыть</Button>
                </Modal.Footer>
            </Modal>
        );
    };
}



const mapStateToProps = (state) => ({
    users: []
});

export default connect (mapStateToProps, null) (AddUserInProjectModal);
