import React, {Component} from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import { connect } from 'react-redux';
import axios from 'axios';
import Moment from 'react-moment';
import * as css from './StylesUserPage.css';


class UserPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: {
                first_name: '',
                last_name: '',
                is_admin: '',
                birth_date: new Date()
            },
            isRequested: false
        };
    }

    componentDidMount = () => {
        this.getUserInfoHandler("");
    };

    getUserInfoHandler = (apikey) => {
        axios.get('http://localhost:57392/singleUser?apikey=qwerty')
        .then(response => {
            console.log(response);
            this.setState({user: response.data.data, isRequested: true});
        });
    }
    render() {
        if (this.state.isRequested) {
            return <div className="main">
            <div className="heading">
                Информация о пользователе
                <Button onClick={this.handlerSave} variant="primary" type="submit">
                        Удалить
                </Button>
            </div>
            <Form>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <Form.Label className="boldLineHead" column sm={2}>
                        Имя
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Label column sm={2}>
                            {
                                this.state.user.first_name
                            }
                        </Form.Label>
                    </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <Form.Label className="boldLineHead" column sm={2}>
                        Фамилия
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Label column sm={2}>
                            {
                                this.state.user.last_name
                            }
                        </Form.Label>
                    </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <Form.Label className="boldLineHead" column sm={2}>
                        Номер телефона
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Label column sm={2}>
                            {
                                this.state.user.phone
                            }
                        </Form.Label>
                    </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <Form.Label className="boldLineHead" column sm={2}>
                        Email
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Label column sm={2}>
                            {
                                this.state.user.email
                            }
                        </Form.Label>
                    </Col>
                </Form.Group>
                <Form.Group as={Row} controlId="formHorizontalEmail">
                    <Form.Label className="boldLineHead" column sm={2}>
                        Дата рождения
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Label column sm={2}>
                            <Moment format="DD.MM.YYYY">
                                    {
                                        new Date(this.state.user.birth_date)
                                    }
                            </Moment>
                        </Form.Label>
                    </Col>
                </Form.Group>

            </Form>
        </div>;
        } else {
            return <div>Loading...</div>
        }
    };
}

const mapStateToProps = (state) => ({
    user: state.User
});

export default connect(
    mapStateToProps,
    null
) (UserPage);
