import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import * as css from "./TaskStyles.css";
import { connect } from 'react-redux';
import axios from 'axios';
import Moment from 'react-moment';
import DeleteConfirmModal from '../DeleteConfirmModal';


class Task extends Component {
    constructor(props) {
        super(props);
        this.state = {
            task: {
            },
            taskId: Number.parseInt(props.location.pathname.substring(props.location.pathname.lastIndexOf('/') + 1)),
            isRequested: false,
            isShow: false
        };
    }

    componentDidMount = () => {
        this.getUserInfoHandler("");
    };

    setShow = (value) => {
        this.setState({ isShow: value });
    }

    getUserInfoHandler = (apikey) => {
        axios.get('http://localhost:57392/singleTask?apikey=qwerty&task_id=' + this.state.taskId)
            .then(response => {
                console.log(response);
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.setState({ task: response.data.data, isRequested: true });
                }
            });
    }

    deleteTaskHandler = (apikey) => {
        axios.post('http://localhost:57392/taskJobs',
            {
                apikey: "qwerty",
                task_id: this.state.taskId
            })
            .then(response => {
                console.log(response);
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.setShow(false);
                    this.props.history.push(`/tasks`);
                }
            });
    }

    render() {
        if (this.state.isRequested) {
            return <div className="main">
                <div className="inline_block_top">
                    <div className="text_title_ag">Информация о задаче: </div>
                    <Button className="button_padding" variant="outline-primary">Удалить задачу</Button>
                </div>
                <Form>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Название
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.task_name
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Описание
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.task_description
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Датa постановки
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.date_create
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Дата дедлайна
                    </Form.Label>
                        <Col sm={8}>
                            {
                                this.state.task.date_deadline && <Form.Label column sm={12}>
                                    <Moment format="DD.MM.YYYY">
                                        {
                                            this.state.task.date_deadline
                                        }
                                    </Moment>
                                </Form.Label>
                            }

                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Дата завершения
                    </Form.Label>
                        <Col sm={8}>
                            {
                                this.state.task.date_finished && <Form.Label column sm={12}>
                                    <Moment format="DD.MM.YYYY">
                                        {
                                            new Date(this.state.task.date_finished)
                                        }
                                    </Moment>
                                </Form.Label>
                            }
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Исполнитель
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.user_first_name +
                                    " " + this.state.task.user_last_name
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Создатель задачи
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.create_first_name + " " + this.state.task.creater_last_name
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label className="boldLineHead" column sm={4}>
                            Статус
                    </Form.Label>
                        <Col sm={8}>
                            <Form.Label column sm={12}>
                                {
                                    this.state.task.task_status_text
                                }
                            </Form.Label>
                        </Col>
                    </Form.Group>
                </Form>

                <DeleteConfirmModal show={this.state.isShow}
                    projectId={this.state.projectId}
                    onHide={() => this.setShow(false)}
                    onSave={(projectId) => this.deleteTaskHandler("")}
                />

            </div>;
        } else {
            return <div>Loading...</div>
        }
    };
}

const mapStateToProps = (state) => ({
    task: state.Task,
    taskId: 0,
    isShow: false
});

export default connect(
    mapStateToProps,
    null
)(Task);
