import React, { Component } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { connect } from 'react-redux';



class CreateProjectModal extends Component {
    render() {
        return (
            <Modal
                {...props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Создание проекта
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        
                        <Form.Group controlId="name">
                            <Form.Label>Название</Form.Label>
                            <Form.Control placeholder="Название проекта" />
                        </Form.Group>

                        <Form.Group controlId="description">
                            <Form.Label>Описание</Form.Label>
                            <Form.Control placeholder="Описание" />
                        </Form.Group>

                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={props.onHide} variant="primary" type="submit">
                        Сохранить
                    </Button>
                    <Button onClick={props.onHide}>Закрыть</Button>
                </Modal.Footer>
            </Modal>
        );
    };
}

const mapStateToProps = (state) => ({
    projects: state.Projects,
    isShow: false,
    isRequested: state.Projects ? state.Projects.IsRequested : false
});

const mapDispatchToProps = (dispatch) => {
    return {
        projectsRequest: () => {
            dispatch(ActionTypes.ActionTypes.projectsActions.projectsActions.projectsRequest());
        },
        projectsSaveData: (action) => {
            dispatch(ActionTypes.ActionTypes.projectsActions.projectsActions.projectsSaveData(action));
        }
    }
};

export default connect (mapStateToProps, mapDispatchToProps) (CreateProjectModal);
