import React, { Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import CreateProjectModal from '../CreateProjectModal';
import { connect } from 'react-redux';
import ActionTypes from '../../store/Actions/index.js';
import axios from 'axios';
import * as css from "./TimesheetsStyles.css";
import Moment from 'react-moment';


class Timesheets extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tasks: {
                admin_task_list: [],
                new_tasks: [],
                dev_tasks: [],
                qa_tasks: [],
                ready_tasks: []
            },
            isShow: false,
            isRequested: false
        };
    }

    componentDidMount = () => {
        this.getTimesheetsHandler("");
    };

    getTimesheetsHandler = (apikey, userId) => {
        userId = 1;
        axios.get('http://localhost:57392/timesheet?apikey=qwerty&user_id=' + userId)
            .then(response => {
                console.log(response);
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.setState({ tasks: response.data.data, isRequested: true });
                }
            });
    }

    createNewProjectHandler = (name, descr) => {
        axios.post('http://localhost:57392/project',
            {
                apikey: 'qwerty',
                project_name: name,
                description: descr
            }).then(response => {
                if (response.data.error) {
                    alert(response.data.error);
                } else {
                    this.setShow(false);
                    this.getProjectsListHandler("");
                }
            });
    }

    setShow = (value) => {
        this.setState({ isShow: value });
    }


    render() {
        if (this.state.isRequested) {
            return <div className="main_fon">
                <div className="inline_block">
                    <div className="text_title_ag">Отчеты по времени: </div>
                </div>
                <ListGroup>
                    <ListGroup.Item className="inline_block">
                        <div className="inline_element">Название проекта</div>
                        <div className="inline_element">Дата создания</div>
                        <div className="inline_element">Количество пользователей</div>
                        <div className="inline_element">Создатель</div>
                    </ListGroup.Item>
                    {
                        this.state.tasks.new_tasks.map(task => (
                            <ListGroup.Item className="inline_block">
                                <a className="inline_element" href={"/task/" + task.task_id}>{task.title}</a>
                                <div className="inline_element">
                                    <Moment format="DD.MM.YYYY">
                                        {
                                            new Date(task.date_create)
                                        }
                                    </Moment>
                                </div>
                                <div className="inline_element">{task.hours_count}</div>
                                <div className="inline_element">{task.create_first_name + ' ' + task.creater_last_name}</div>
                            </ListGroup.Item>
                        ))
                    }
                </ListGroup>

                <CreateProjectModal show={this.state.isShow}
                    onHide={() => this.setShow(false)}
                    onSave={(name, descr) => this.createNewProjectHandler(name, descr)} />
            </div>;
        } else {
            return <div>Loading...</div>
        }

    };
}

const mapStateToProps = (state) => ({
    tasks: state.Tasks,
    isShow: false,
    isRequested: state.Tasks ? state.Tasks.IsRequested : false
});

export default connect(
    mapStateToProps,
    null
)(Timesheets);
