import React, { Component } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { connect } from 'react-redux';
import { DropdownButton, Dropdown, InputGroup } from 'react-bootstrap';


class ChangeTaskStatusModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            projectId: props.projectId
        };
    }

    render() {
        return (
            <Modal
                show={this.props.show}
                onHide={this.props.onHide}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Удаление проекта
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group controlId="date">
                            <Form.Label>Дата</Form.Label>
                            <Form.Control onChange={this.changeDateHandler} placeholder="Укажите дату" />
                        </Form.Group>

                        <Form.Group controlId="date">
                            <Form.Label>Статус</Form.Label>
                            <DropdownButton
                                as={InputGroup.Prepend}
                                variant="outline-secondary"
                                title="Dropdown"
                                id="input-group-dropdown-1"
                            >
                                <Dropdown.Item href="#">Action</Dropdown.Item>
                                <Dropdown.Item href="#">Another action</Dropdown.Item>
                                <Dropdown.Item href="#">Something else here</Dropdown.Item>
                                <Dropdown.Divider />
                                <Dropdown.Item href="#">Separated link</Dropdown.Item>
                            </DropdownButton>
                        </Form.Group>


                        <Form.Group controlId="date">
                            <Form.Label>Дата</Form.Label>
                            <Form.Control onChange={this.changeDateHandler} placeholder="Укажите дату" />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        onClick={() => this.props.onSave(this.props.projectId)}
                        variant="primary"
                        type="submit">
                        Выполнить
                    </Button>
                    <Button onClick={this.props.onHide}>Закрыть</Button>
                </Modal.Footer>
            </Modal>
        );
    };
}

const mapStateToProps = (state) => ({

});

export default connect(mapStateToProps, null)(ChangeTaskStatusModal);
