import React, { Component } from 'react';
import * as css from "./Styles.css";
import { connect } from 'react-redux';
import ActionTypes from '../../store/Actions/index.js';
import axios from 'axios';


class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            login: "",
            password: "",
            User: {}
        };
    }

    handlerEmail = (event) => {
        this.setState({ login: event.target.value })
    } 

    handlerPassword = (event) => {
        this.setState({ password: event.target.value })
    } 

    createUserHandler = () => {
        if (this.state.login === null ||
            this.state.login === "" ||
            this.state.password === null ||
            this.state.password === "") {
                alert("Некорректные данные!");
            } else {
                axios.post('http://localhost:57392/authUser',
                {
                    login: "",
                    password: ''
                }).then(response => {
                    if (response.data.error) {
                        alert(response.data.error);
                    } else {
                        this.setState(this.state.User, {
                            user_id: response.data.data.user_id,
                            apikey:  response.data.data.apikey,
                            is_admin:  response.data.data.is_admin,
                        });
                        this.props.history.push(`/users`);
                    }
                    this.setShow(false);
                    this.getUsersListHandler("");
                });
            }
    }


    render() {
        console.log(this.props);

        console.log(this.state);

        return <div className="auth-window">
            <div className="container">
                <div className="row">
                    <div className="col-md-offset-3 col-md-6">
                        <form className="form-horizontal">
                            <span className="heading">АВТОРИЗАЦИЯ</span>
                            <div className="form-group">
                                <input type="email" className="form-control" id="inputEmail" onChange={this.handlerEmail} placeholder="E-mail" />
                                <i className="fa fa-user"></i>
                            </div>
                            <div className="form-group help">
                                <input type="password" className="form-control" id="inputPassword" onChange={this.handlerPassword} placeholder="Password" />
                                <i className="fa fa-lock"></i>
                            </div>
                            <div className="form-group button-padding">
                                <div className="main-checkbox">
                                    <input type="checkbox" value="none" id="checkbox1" name="check" />

                                </div>
                                <button type="submit" onClick={this.createUserHandler} className="btn btn-default">ВХОД</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>;
    }
}

const mapStateToProps = (state) => ({
    user: state.User,
    login: state.login,
    password: state.password
});

export default connect(
    mapStateToProps,
    null
)
(Login);
